mapper.select:
SELECT count(1) from tb_comment c LEFT JOIN tb_user_info u on c.user_id = u.id
c.type = #{condition.type}
and c.is_review = #{condition.isReview}
and u.nickname like concat('%',#{condition.keywords},'%')

SELECT c.id, u.avatar, u.nickname, r.nickname AS reply_nickname, a.article_title, c.comment_content, c.type, c.create_time, c.is_review FROM tb_comment c LEFT JOIN tb_article a ON c.topic_id = a.id LEFT JOIN tb_user_info u ON c.user_id = u.id LEFT JOIN tb_user_info r ON c.reply_user_id = r.id
c.type = #{condition.type}
and c.is_review = #{condition.isReview}
and u.nickname like concat('%',#{condition.keywords},'%')
ORDER BY id DESC LIMIT #{current},#{size}

SELECT c.id, c.user_id, c.comment_content, c.create_time, ui.nickname, ui.avatar, ui.web_site FROM tb_comment c LEFT JOIN tb_user_info ui ON ui.id = c.user_id
topic_id = #{commentVO.topicId}
AND type = #{commentVO.type} AND c.is_review = 1 AND parent_id IS NULL
c.is_review = 1 AND c.is_delete = 0 AND c.parent_id = NULL ORDER BY c.id DESC LIMIT #{current}, #{size}
